#![expect(clippy::unwrap_used, clippy::indexing_slicing)]

use super::*;
use aidoku_test::aidoku_test;
use paste::paste;
use spin::Lazy;

macro_rules! listing {
	($name:ident, $index:literal, $title:literal, $first_manga:expr, $last_chapter:expr, $id:literal, $listing_name:literal) => {
		paste! {
			#[aidoku_test]
			fn [<listing_ $name>]() {
				let home_component = &HOME_LAYOUT.components[$index];
				assert_eq!(home_component.title.as_deref().unwrap(), $title);

				let HomeComponentValue::MangaChapterList {
					page_size,
					entries,
					listing,
				} = &home_component.value
				else {
					panic!("Not a manga chapter list");
				};
				assert_eq!(page_size.unwrap(), 2);

				assert_eq!(entries[0].manga, $first_manga);
				assert_eq!(entries[0].chapter, $last_chapter);

				assert_eq!(
					*listing.as_ref().unwrap(),
					Listing {
						id: $id.into(),
						name: $listing_name.into(),
						..Default::default()
					}
				);
			}
		}
	};
}
